INSERT IGNORE INTO post(title, content, member_id, team_id) VALUES
    ('경기대 근처 어르신 산책 도와드릴 분 계신가요?', '이번 주말 오전에 간단한 산책 도움 요청이 있어요! 시간 되시는 분 계실까요?', 6, 1),  -- 자원봉사자
    ('응급 대응 매뉴얼 공유합니다', '간단한 응급 대응 요약본을 정리해봤어요. 혹시 더 보완할 점 있으면 알려주세요!', 11, 1),  -- 가족
    ('간병 일지 작성법 공유해요', '학교 과제로 시작했는데, 실제 현장에서 어떻게 활용되는지 궁금해서 글 남겨요.', 12, 1);  -- 자원봉사자
INSERT IGNORE INTO post(title, content, member_id, team_id) VALUES
    ('아파트 복도 미끄럼 조심하세요', '지난주 어르신 한 분이 미끄러질 뻔했어요. 다들 주의해주세요!', 5, 2),  -- 가족
